<nav
  class="nav"
  style:position="fixed"
  style:z-index="99"
  style:display="flex"
  style:gap="1.5rem"
  style:justify-content="stretch"
  style:flex-flow="row wrap"
  style:padding="30px 27px"
  style:width="100vw"
  style:box-sizing="border-box"
  style:font-size="24px"
  style:letter-spacing="0"
  style:line-height="34px"
  style:cursor="pointer"
  style:pointer-events="none"
>
  <div
    style:display="flex"
    style:justify-content="space-between"
    style:width="100%"
  >
    <a href="/" style:cursor="pointer" style={fontStyle} style:pointer-events="auto">FAR—NEAR</a>

    <div style:display="flex" style:gap="2rem" style:pointer-events="auto">
      <a style={fontStyle} href="/about"> About </a>
      <a style={fontStyle} href="/newsletter"> Newsletter </a>
      <a style={fontStyle} href="/shop"> Store </a>
      <a style={fontStyle} href="/cart"> Cart </a>
    </div>
  </div>

  <div
    style:display="flex"
    style:justify-content="space-between"
    style:width="100%"
  >
    <div />

    <div
      style:display="flex"
      style:border-radius="30px"
      style:border="1px solid white"
      style:overflow="hidden"
      style:width="160px"
      style:position="relative"
      style:pointer-events="auto"
    >
      <div
      on:click={() => $onTop = 'map'}
      on:mouseenter={() => mouseover = 'map'}
      on:mouseleave={() => mouseover = false}
      style:padding="12px 16px 12px 12px"
        style:text-align="right"
        style:opacity={$onTop === 'map' || mouseover === 'map' ? 1 : 0.7}
        style:width="50%"
        style={fontSmallStyle}
        style:user-select="none"
      >
        Map
      </div>
      <div style="height: 100%; border-right: 1px solid white; display: block;"></div>
      <div
        on:click={() => $onTop = 'walk'}
        on:mouseenter={() => mouseover = 'walk'}
        on:mouseleave={() => mouseover = false}
        style:opacity={$onTop === 'walk' || mouseover === 'walk' ? 1 : 0.7}
        style:user-select="none"
        style:padding="12px 12px 12px 16px"
        style:text-align="left"
        style:width="50%"
        style={fontSmallStyle}
      >
        Walk
      </div>
    </div>
  </div>
</nav>
